(51, 'custom_deposit_schedules_inbox', 'marketing', 'en_US', 'Customize your deposit schedule', 'Receive your funds according to your own business needs with custom deposit schedules from WooCommerce Payments. You’ll have the flexibility to deposit funds into your bank account daily, weekly, monthly, or even on-demand – you’ve done the work, now get paid on your terms.', '{}', 'pending', 'woocommerce.com', '2022-09-16 22:07:06', NULL, 0, 'plain', '', 0, 0, 'info'),
(52, 'googlelistings_signals2022_hasGLA', 'marketing', 'en_US', 'Show off your products with Ads on Google', 'You’re ready to grow with ads. Google optimizes for performance across your products; you only pay for results. If you’re new to Google, you can earn up to $500 in ad credits (T&amp;Cs apply). <a href=\"https://woocommerce.com/my-account/create-a-ticket/\">Contact support</a> if you need guidance with Google Listings &amp; Ads.', '{}', 'pending', 'woocommerce.com', '2022-09-23 02:33:39', NULL, 0, 'plain', '', 0, 0, 'info'),
